package com.kearmy.dao;import com.kearmy.domain.QueryVo;import com.kearmy.domain.User;import java.util.List;public interface IUserDao {    /**     * 查询所有用户     * @return     */    List<User> findAll();    /**     * 根据用户id查询     * @param userId     * @return     */    User findById(Integer userId);    /**     * 根据用户名模糊查询所有用户     * @param username     * @return     */    List<User> findByUsername(String username);    /**     * 查询总记录数     * @return     */    Integer findTotal();    /**     * 通过QueryVO模糊查询查询     * @param queryVo     * @return     */    List<User> findByQueryVo(QueryVo queryVo);    /**     * 通过传入不同的参数动态查询，If标签     * @param user     * @return     */    List<User> findByConditionIf(User user);    /**     * 动态SQL语句，where标签     * @param user     * @return     */    List<User> findByConditionWhere(User user);    /**     * 通过id集合查询所有用户，foreach标签     * @param queryVo     * @return     */    List<User> findInIds(QueryVo queryVo);}