package com.kearmy.domain;/** * 使用子类的方式实现连接查询，这种方式使用的比较少 */public class AccountUser extends Account {    private String username;    private String address;    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }    @Override    public String toString() {        return super.toString()+"      AccountUser{" +                "username='" + username + '\'' +                ", address='" + address + '\'' +                '}';    }}