package com.kearmy.domain;/** * 一个用户user可以有多个账户account * 针对其中一个账户只能有唯一一个用户 * * mybatis多对一看成一对一 * * 从构件查询方式中，我们可以看出，在账户（多的一方）实体类中包含用户（一的一方）实体类 * 那么在查询的最后，针对一个账户可以查询出一个用户的信息 */public class Account {    private Integer id;    private Integer uid;    private Double money;    private User user;    public User getUser() {        return user;    }    public void setUser(User user) {        this.user = user;    }    public Integer getId() {        return id;    }    public void setId(Integer id) {        this.id = id;    }    public Integer getUid() {        return uid;    }    public void setUid(Integer uid) {        this.uid = uid;    }    public Double getMoney() {        return money;    }    public void setMoney(Double money) {        this.money = money;    }    @Override    public String toString() {        return "Account{" +                "id=" + id +                ", uid=" + uid +                ", money=" + money +                '}';    }}