package com.kearmy.main;import com.kearmy.dao.IAccountDao;import com.kearmy.domain.Account;import org.apache.ibatis.io.Resources;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.apache.ibatis.session.SqlSessionFactoryBuilder;import org.junit.After;import org.junit.Before;import org.junit.Test;import java.io.IOException;import java.io.InputStream;import java.util.List;public class TestAccount {    private InputStream is;    private SqlSession sqlSession;    IAccountDao iAccountDao;    @Before    public void init() throws IOException {        is = Resources.getResourceAsStream("SqlMapConfig.xml");        SqlSessionFactory sqlSessionFactory = new SqlSessionFactoryBuilder().build(is);        sqlSession = sqlSessionFactory.openSession();    }    @After    public void destory() throws IOException {        sqlSession.close();        is.close();    }    @Test    public void testFindAll(){        iAccountDao = sqlSession.getMapper(IAccountDao.class);        List<Account> accounts = iAccountDao.findAll();        for(Account account : accounts){            System.out.println(account);            System.out.println(account.getUser());        }    }}