package com.kearmy.main;import com.kearmy.dao.IAccountDao;import com.kearmy.dao.IUserDao;import com.kearmy.domain.Account;import com.kearmy.domain.User;import org.apache.ibatis.io.Resources;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.apache.ibatis.session.SqlSessionFactoryBuilder;import org.junit.After;import org.junit.Before;import org.junit.Test;import java.io.IOException;import java.io.InputStream;import java.util.List;public class TestUser {    private InputStream is;    private SqlSession sqlSession;    IUserDao iUserDao;    @Before    public void init() throws IOException {        is = Resources.getResourceAsStream("SqlMapConfig.xml");        SqlSessionFactory sqlSessionFactory = new SqlSessionFactoryBuilder().build(is);        sqlSession = sqlSessionFactory.openSession();    }    @After    public void destory() throws IOException {        sqlSession.close();        is.close();    }    @Test    public void testFindAll(){        iUserDao = sqlSession.getMapper(IUserDao.class);        List<User> users = iUserDao.findAll();        for(User user : users){            System.out.println(user);            System.out.println(user.getAccounts());        }    }}