package com.kearmy.dao;import com.kearmy.domain.User;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import java.util.List;/** * 注解开发 * 支持@Select @Insert @Update @Delete */public interface IUserDao {    /**     * 查询所有用户信息     * @return     */    @Select("select * from user")    List<User> findAll();    /**     * 插入用户     * @param user     */    @Insert("insert into user(username, address, sex, birthday) values(#{username}, #{address}, #{sex}, #{birthday}) ")    void insertUser(User user);    @Update("update user set username=#{username}, address=#{address}, sex=#{sex}, birthday=#{birthday} where id=#{id}")    void updateUser(User user);}