package com.kearmy;import com.kearmy.dao.IAccountDao;import com.kearmy.dao.IUserDao;import com.kearmy.domain.Account;import com.kearmy.domain.User;import org.apache.ibatis.io.Resources;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.apache.ibatis.session.SqlSessionFactoryBuilder;import org.junit.After;import org.junit.Before;import org.junit.Test;import java.io.IOException;import java.io.InputStream;import java.util.List;public class TestAccount {    private InputStream is;    private SqlSession sqlSession;    private IAccountDao iAccountDao;    @Before    public void init() throws IOException {        is = Resources.getResourceAsStream("SqlMapConfig.xml");        SqlSessionFactory sqlSessionFactory = new SqlSessionFactoryBuilder().build(is);        sqlSession = sqlSessionFactory.openSession();        iAccountDao = sqlSession.getMapper(IAccountDao.class);    }    @After    public void destory() throws IOException {        sqlSession.commit();        sqlSession.close();        is.close();    }    /**     * 测试查询所有用户     */    @Test    public void testFindAll(){        List<Account> accounts = iAccountDao.findAll();        for(Account account : accounts){            System.out.println(account);            System.out.println(account.getUser());        }    }}