package com.kearmy;import com.kearmy.dao.IUserDao;import com.kearmy.domain.User;import org.apache.ibatis.io.Resources;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.apache.ibatis.session.SqlSessionFactoryBuilder;import org.junit.After;import org.junit.Before;import org.junit.Test;import java.io.IOException;import java.io.InputStream;import java.util.Date;import java.util.List;public class TestUser {    private InputStream is;    private SqlSession sqlSession;    private IUserDao iUserDao;    @Before    public void init() throws IOException {        is = Resources.getResourceAsStream("SqlMapConfig.xml");        SqlSessionFactory sqlSessionFactory = new SqlSessionFactoryBuilder().build(is);        sqlSession = sqlSessionFactory.openSession();        iUserDao = sqlSession.getMapper(IUserDao.class);    }    @After    public void destory() throws IOException {        sqlSession.commit();        sqlSession.close();        is.close();    }    /**     * 测试查询所有用户     */    @Test    public void testFindAll(){        List<User> userList = iUserDao.findAll();        for(User user : userList){            System.out.println(user);            System.out.println(user.getAccounts());        }    }    /**     * 测试通过id查询单个用户     */    @Test    public void testFindOne(){        User user = iUserDao.findOne(43);        System.out.println(user);    }}